import axios from 'axios';
import Cookies from 'js-cookie';
import actions from './actions';

const { addNewSubAdmin, getListSubAdminBegin, getSubAdmin } = actions;

const addSubAdmin = data => {
  return async dispatch => {
    try {
      dispatch(getListSubAdminBegin());

      const response = await axios.post('http://20.218.124.106:1885/api/v1/admin/addSubAdmin', data, {
        headers: {
          'Content-Type': 'application/json',
          token: Cookies.get('token'),
        },
      });
      if (response.status === 200) dispatch(addNewSubAdmin(response.data));
    } catch (err) {
      dispatch(addNewSubAdmin(err.response.data));
    }
  };
};

const getListSubAdmin = (pages, limted) => {
  return async dispatch => {
    dispatch(getListSubAdminBegin());
    const response = await axios.get(
      `http://20.218.124.106:1885/api/v1/admin/listSubAdmin?page=${pages}&limit=${limted}`,
      {
        headers: {
          'Content-Type': 'application/json',
          token: Cookies.get('token'),
        },
      },
    );
    dispatch(getSubAdmin(response.data.result));
  };
};

const deleteSubAdmin = id => {
  return async dispatch => {
    await axios.delete(`http://20.218.124.106:1885/api/v1/admin/deleteSubAdmin?subAdminId=${id}`, {
      headers: {
        'Content-Type': 'application/json',
        token: Cookies.get('token'),
      },
    });
    dispatch(deleteAdmin(id));
  };
};

export { addSubAdmin, getListSubAdmin, deleteSubAdmin };
