/* eslint-disable prettier/prettier */
/* eslint-disable no-unused-vars */
/* eslint-disable react/prop-types */
import React, { useState } from 'react';
import { Spin, Table, Empty, notification } from 'antd';
import FeatherIcon from 'feather-icons-react';
import moment from 'moment';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { UserTableStyleWrapper } from '../pages/style';
import { TableWrapper } from '../styled';
import Heading from '../../components/heading/heading';
import { Button } from '../../components/buttons/buttons';
import { Cards } from '../../components/cards/frame/cards-frame';

import {
    deleteAdvertisement,
    getListAdvertisement,
    getAdvertisementById,
} from '../../redux/advertisement/actionCreator';
import { SpinerWraperStyle } from '../ui-elements/ui-elements-styled';
import { deleteNews } from '../../redux/news/actionCreator';

const NewsTable = ({ data }) => {
    const { loading } = useSelector(state => state.News);

      const dispatch = useDispatch();

    //   const { totalItems, loading, notFound } = useSelector(state => state.Advertisement);

    //   const [state, setState] = useState({ visible: false, modalType: 'view', colorModal: false });

    const usersTableData = [];


    // حوار تعبان 
    // eslint-disable-next-line no-unused-expressions
    data?.result?.map(categorys => categorys?.news?.map(category => {
        const { status, title, description, updatedAt, createdAt, _id } = category;
        return usersTableData.push({
            key: _id,
            title: (<div className="user-info">
                <Heading className="user-name" as="h6">
                    {title.length > 12 ? `${title.slice(0, 12)}...` : "title"}
                </Heading>
            </div>
            ),
            createdAt: moment(createdAt).format('DD/MM/YYYY HH:mm'),
            updatedAt: moment(updatedAt).format('DD/MM/YYYY HH:mm'),
            status,
            description,
            category: categorys.category,
            action: (
                <div className="table-actions">
                    <>
                        <Link
                            className="btn-icon"
                            type="info"
                            shape="circle"
                        >
                            <FeatherIcon icon="edit" size={16} style={{ color: '#ADB4D2' }} />
                        </Link>
                        <Button
                            className="btn-icon"
                            type="danger"
                            onClick={() => {
                                dispatch(deleteNews(_id, categorys.category));
                            }}
                            to="#"
                            shape="circle"
                        >
                            <FeatherIcon icon="trash-2" size={16} />
                        </Button>
                    </>
                </div>
            ),

        })
    }))

    const adsTableColumns = [
        {
            title: 'Title',
            dataIndex: 'title',
            key: 'title',
        },
        {
            title: 'Created At',
            dataIndex: 'createdAt',
            key: 'createdAt',
        },
        {
            title: "Updated At",
            dataIndex: 'updatedAt',
            key: 'updatedAt',
        },
        {
            title: "Status",
            dataIndex: 'status',
            key: 'status',
        },
        {
            title: "Category",
            dataIndex: 'category',
            key: 'category',
        },
        {
            title: "Description",
            dataIndex: 'description',
            key: 'description',
        },
        {
            title: 'Actions',
            dataIndex: 'action',
            key: 'action',
            width: '90px',
        },

    ];

    return (
        <Cards headless>
            <UserTableStyleWrapper>
                <TableWrapper className="table-responsive">
                    <Table
                        dataSource={usersTableData}
                        columns={adsTableColumns}
                        pagination={false}
                    />
                </TableWrapper>
            </UserTableStyleWrapper>
        </Cards>
    );
};

export default NewsTable;
