/* eslint-disable prettier/prettier */
import axios from 'axios';
import Cookies from 'js-cookie';
import actions from './actions';

const { addNews, getListNewsBegin, getnewsItem, deleteNewsItem, searchNewsItems, editNewsItem } = actions;

const addNewNews = data => {
    return async dispatch => {
        try {
            dispatch(getListNewsBegin());
            const response = await axios.post('http://20.218.124.106:1885/api/v1/admin/addNews', data, {
                headers: {
                    'Content-Type': 'application/json',
                    token: Cookies.get('token'),
                },
            });
            if (response.status === 200) dispatch(addNews(response.data));
        } catch (err) {
            dispatch(addNews(err.response.data));
        }
    };
};


const getNews = (category) => {
    return async dispatch => {
        try {
            const response = await axios.get(`http://20.218.124.106:1885/api/v1/admin/listNews?category=${category}`, {
                headers: {
                    'Content-Type': 'application/json',
                    token: Cookies.get('token'),
                },
            });
            dispatch(getnewsItem(response.data.result))
        }
        catch (err) {
            console.log(err)
        }

    }
}

const deleteNews = (id, categoryName) => {
    return async dispatch => {
        try {
            const response = await axios.delete(`http://20.218.124.106:1885/api/v1/admin/deleteNews?category=${categoryName}&id=${id}`, {
                headers: {
                    'Content-Type': 'application/json',
                    token: Cookies.get('token'),
                },
            });
            dispatch(deleteNewsItem(id))
        }
        catch (err) {
            console.log(err)
        }

    }
}

const searchNews = (key) => {
    return async dispatch => {
        try {
            const response = await axios.get(`http://20.218.124.106:1885/api/v1/admin/listNews?search=${key}`, {
                headers: {
                    'Content-Type': 'application/json',
                    token: Cookies.get('token'),
                },
            });
            dispatch(searchNewsItems(response.data.result))
        }
        catch (err) {
            console.log(err)
        }

    }
}

const editNews = (data) => {
    return async dispatch => {
        try {
            const response = await axios.put(`http://20.218.124.106:1885/api/v1/admin/updateNews`, data, {
                headers: {
                    'Content-Type': 'application/json',
                    token: Cookies.get('token'),
                },
            })
            if (response.status === 200) dispatch(editNewsItem(response.data));
        }
        catch (err) {
            dispatch(editNewsItem(err.response.data));
        }
    }
}

export { addNewNews, getNews, deleteNews, searchNews, editNews };
