/* eslint-disable no-underscore-dangle */
/* eslint-disable prettier/prettier */
import axios from 'axios';
import Cookies from 'js-cookie';
import actions from './actions';

const { getAdvertisement, deleteAds } = actions;

// get list 
const getListAdvertisement = () => {
    return async dispatch => {
        try {
            const response = await axios.get('https://api.develocity.finance/api/v1/admin/advertisementList?limit=9',
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'token': Cookies.get('token')
                    }
                });
            dispatch(getAdvertisement(response.data.result.docs));
        } catch (err) {
            console.log(err);
        }
    };
};

const deleteAdvertisement = (id) => {
    return async dispatch => {
        try {
            const response = await axios.delete(`https://api.develocity.finance/api/v1/admin/deleteAdvertisment/${id}`,
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'token': Cookies.get('token')
                    }
                });
            dispatch(deleteAds(response.data.result.deleted._id));
        } catch (err) {
            console.log(err);
        }
    };
}




export { getListAdvertisement, deleteAdvertisement };
