/* eslint-disable react/prop-types */
import React, { useState } from 'react';
import { Spin, Table, Empty, notification } from 'antd';
import FeatherIcon from 'feather-icons-react';
import moment from 'moment';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import AdsModalEdit from './AdsModalEdit';
import { UserTableStyleWrapper } from '../pages/style';
import { TableWrapper } from '../styled';
import Heading from '../../components/heading/heading';
import { Button } from '../../components/buttons/buttons';
import { Cards } from '../../components/cards/frame/cards-frame';
import {
  deleteAdvertisement,
  getListAdvertisement,
  getAdvertisementById,
} from '../../redux/advertisement/actionCreator';
import { SpinerWraperStyle } from '../ui-elements/ui-elements-styled';

const AdvertisementTable = ({ data }) => {
  const dispatch = useDispatch();
  const { totalItems, loading, notFound } = useSelector(state => state.Advertisement);

  const [state, setState] = useState({ visible: false, modalType: 'view', colorModal: false });


  const openNotificationWithIcon = type => {
    notification[type]({
      message: 'Notification',
      description: "sssssssssssss"
    });
  };



  const showModal = type => {
    setState({
      visible: true,
      modalType: type,
    });
  };
  const handleCancel = () => {
    setState({
      visible: false,
      colorModal: false,
    });
  };

  const usersTableData = [];

  data.map(ad => {
    const { _id, image, title, startDate, endDate, adPosition, status } = ad;

    return usersTableData.push({
      key: _id,
      title: (
        <div className="user-info">
          <figure>
            <img style={{ width: '40px' }} src={image} alt="" />
          </figure>
          <figcaption>
            <Heading className="user-name" as="h6">
              {title.length > 12 ? `${title.slice(0, 12)}...` : title}
            </Heading>
          </figcaption>
        </div>
      ),
      startdate: moment(startDate).format('DD/MM/YYYY HH:mm'),
      enddate: moment(endDate).format('DD/MM/YYYY HH:mm'),
      adposition: adPosition,
      status,
      action: (
        <div className="table-actions">
          <>
            <Button
              className="btn-icon"
              onClick={() => {
                dispatch(getAdvertisementById(_id)).then(() => {
                  showModal('primary');
                });
              }}
              type="view"
              shape="circle"
            >
              <FeatherIcon icon="eye" size={16} />
            </Button>

            {state.visible && (
              <AdsModalEdit modalType={state.modalType} visible={state.visible} handleCancel={handleCancel} />
            )}

            <Link
              className="btn-icon"
              type="info"
              to={status === 'ACTIVE' && `/admin/editAdvertisement/${_id}`}
              onClick={() => console.log('edit')}
              shape="circle"
            >
              {/* cant update when status is finished */}
              <FeatherIcon icon="edit" size={16} style={{ color: '#ADB4D2' }} />
            </Link>
            <Button
              className="btn-icon"
              type="danger"
              to="#"
              shape="circle"
              onClick={() => {
                dispatch(deleteAdvertisement(_id));
              }}
            >
              <FeatherIcon icon="trash-2" size={16} />
            </Button>
          </>
        </div>
      ),
    });
  });

  const adsTableColumns = [
    {
      title: 'Title',
      dataIndex: 'title',
      key: 'title',
    },
    {
      title: 'StartDate',
      dataIndex: 'startdate',
      key: 'startdate',
    },
    {
      title: 'End Date',
      dataIndex: 'enddate',
      key: 'enddate',
    },
    {
      title: 'Ad Position',
      dataIndex: 'adposition',
      key: 'adposition',
    },

    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
    },
    {
      title: 'Actions',
      dataIndex: 'action',
      key: 'action',
      width: '90px',
    },
  ];

  return (
    <Cards headless>
      <UserTableStyleWrapper>
        <TableWrapper className="table-responsive">
          {loading ? (
            <SpinerWraperStyle>
              <Spin />
            </SpinerWraperStyle>
          ) : !notFound ? (
            <Table
              dataSource={usersTableData}
              columns={adsTableColumns}
              pagination={{
                defaultPageSize: 5,
                total: totalItems,
                showTotal: (total, range) => `${range[0]} - ${range[1]} of ${totalItems} items`,
                onChange: (page, pageSize) => {
                  dispatch(getListAdvertisement(page, pageSize));
                },
              }}
            />
          ) : (
            <Cards title="Basic" caption="The simplest use of Empty">
              <Empty />
            </Cards>
          )}
        </TableWrapper>
      </UserTableStyleWrapper>
    </Cards>
  );
};

export default AdvertisementTable;
