/* eslint-disable prettier/prettier */
import { Button, Col, DatePicker, Form, Input, Row, Select, Upload } from 'antd';
import FormData from 'form-data';
import TextArea from 'antd/lib/input/TextArea';
import { Option } from 'antd/lib/mentions';

import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom/cjs/react-router-dom.min';
import { useDispatch, useSelector } from 'react-redux';
import moment from 'moment';
import { FormWrapperNoMargin } from './Style';
import { Cards } from '../../components/cards/frame/cards-frame';

import { PageHeader } from '../../components/page-headers/page-headers';
import { FormValidationWrap, HorizontalFormStyleWrap, VerticalFormStyleWrap } from '../forms/overview/Style';


import { Main } from '../styled';
import { editTypeAdvertisement, getAdvertisementById, editAdvertisement } from '../../redux/advertisement/actionCreator';


const EditAdvertisement = () => {
    const { adsId } = useParams();
    const [form] = Form.useForm();
    const dispatch = useDispatch();

    const { dataAds, adTypeSelect, loading } = useSelector(state => state.Advertisement);

    const formDataDate = new FormData();
    const formDataImpression = new FormData();
    const formDataClick = new FormData();

    useEffect(() => {
        dispatch(getAdvertisementById(adsId));
    }, [dispatch, adsId]);

    const submitt = (values) => {
        if (adTypeSelect === 'Date') {
            if (dataAds?.title !== values.title) {
                formDataDate.append('title', values.title);
            }
            if (dataAds?.description !== values.description) {
                formDataDate.append('description', values.description);
            }
            if (dataAds?.startDate !== values.startDate) {
                formDataDate.append('startDate', values.startdate);
            }
            if (dataAds?.endDate !== values.endDate) {
                formDataDate.append('endDate', values.enddate);
            }
            if (dataAds?.image.url !== values.avatar.file) {
                formDataDate.append('image', values.avatar.file);
            }

            formDataDate.append('adPosition', values.positionSelect);
            formDataDate.append('adType', values.typeSelect);
            formDataDate.append('viewMode', values.viewMode);
            formDataDate.append('adURL', values.AdURL);
            formDataDate.append('advertismentId', adsId);
            console.log()
            // check if values 
            // dispatch(editAdvertisement(formDataDate));
            // remove form data after submit
            formDataDate.delete('title');
            formDataDate.delete('description');
            formDataDate.delete('startDate');
            formDataDate.delete('endDate');
            formDataDate.delete('image');
            formDataDate.delete('adPosition');
            formDataDate.delete('adType');
            formDataDate.delete('viewMode');
            formDataDate.delete('adURL');
        }
    }

    return (
        <>
            <PageHeader
                ghost
                title="Edit Advertisement"
                buttons={[
                    <Button className="btn-add_new" size="default" type="primary" key="1" >
                        <Link to="/admin/listAdvertisement">List Advertisement</Link>
                    </Button>
                ]} />

            <Main>
                <Row gutter={25}>
                    <Col xs={24}>
                        <FormValidationWrap>
                            <VerticalFormStyleWrap>
                                <Cards title="Custom Styles">
                                    {(dataAds && adTypeSelect !== null) && <Form name="sDash_validation-form" form={form} layout="vertical" onFinish={submitt}>
                                        <Row gutter={30}>
                                            <Col md={8} xs={24}>
                                                <Form.Item name="typeSelect" label="Ad Type" initialValue={
                                                    dataAds.adType
                                                }>
                                                    <Select size="large" className="sDash_fullwidth-select" placeholder="Select Type"
                                                        onChange={(value) => {
                                                            dispatch(editTypeAdvertisement(value))
                                                        }}
                                                    >
                                                        <Option value="Date" >Date</Option>
                                                        <Option value="Impression">Impression</Option>
                                                        <Option value="Click">Click</Option>
                                                    </Select>
                                                </Form.Item>
                                            </Col>
                                            <Col md={8} xs={24}>
                                                <Form.Item name="viewMode" label="View Mode" initialValue={
                                                    dataAds.viewMode
                                                }>
                                                    <Select size="large" className="sDash_fullwidth-select" placeholder="View Mode">
                                                        <Option value="Mobile" >Mobile</Option>
                                                        <Option value="Desktop">Desktop</Option>
                                                    </Select>
                                                </Form.Item>
                                            </Col>

                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="title"
                                                    label="Title"
                                                    initialValue={dataAds.title}
                                                    rules={[
                                                        { min: 10, message: 'must be at least 10 characters!' }
                                                    ]}
                                                >
                                                    <Input placeholder="Enter title" />
                                                </Form.Item>
                                            </Col>
                                            {(adTypeSelect === "Date") &&
                                                <>
                                                    <Col md={8} xs={24}>
                                                        <FormWrapperNoMargin>
                                                            <HorizontalFormStyleWrap className="sDash_input-form">
                                                                <Form.Item name="startdate"
                                                                    label="Start Date"

                                                                    initialValue={moment(dataAds.startDate, 'YYYY-MM-DD')}
                                                                    rules={[{ required: true, message: 'Start Date required!' }]}>
                                                                    <DatePicker placeholder='Start Date' format="MM-DD-YYYY" />
                                                                </Form.Item>
                                                            </HorizontalFormStyleWrap>
                                                        </FormWrapperNoMargin>
                                                    </Col>


                                                    <Col md={8} xs={24}>
                                                        <FormWrapperNoMargin>
                                                            <HorizontalFormStyleWrap className="sDash_input-form">
                                                                <Form.Item name="enddate"
                                                                    label="End Date"
                                                                    initialValue={moment(dataAds.endDate, 'YYYY-MM-DD')}
                                                                    rules={[{ required: true, message: 'End Date required!' }]}>
                                                                    <DatePicker placeholder='End Date'
                                                                        format="MM-DD-YYYY"
                                                                    />
                                                                </Form.Item>
                                                            </HorizontalFormStyleWrap>
                                                        </FormWrapperNoMargin>
                                                    </Col> </>}
                                            {(adTypeSelect === "Impression") &&
                                                <Col md={8} xs={24}>
                                                    <Form.Item
                                                        name="impressionLimit"
                                                        label="Impression Limit"
                                                        initialValue={dataAds.impressionLimit}
                                                        rules={[
                                                            { required: true, message: 'Impression Limit required!' },
                                                            { pattern: /^[0-9]*$/, message: 'must be a number!' },
                                                        ]}
                                                    >
                                                        <Input placeholder="Impression Limit" />
                                                    </Form.Item>
                                                </Col>}

                                            {(adTypeSelect === "Click") && <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="clickLimit"
                                                    label="Click Limit"
                                                    initialValue={dataAds.clickLimit}
                                                    rules={[
                                                        { required: true, message: 'click limit required!' },
                                                        { pattern: /^[0-9]*$/, message: 'must be a number!' },
                                                    ]}
                                                >
                                                    <Input placeholder="Enter Click Limit" />
                                                </Form.Item>
                                            </Col>}



                                            <Col md={8} xs={24}>
                                                <Form.Item name="positionSelect" label="Ad Position" initialValue={
                                                    dataAds.adPosition
                                                }>
                                                    <Select size="large" className="sDash_fullwidth-select" placeholder="Select Position"  >
                                                        <Option value="Home" >Home</Option>
                                                        <Option value="Token">Token</Option>
                                                    </Select>
                                                </Form.Item>
                                            </Col>



                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="AdURL"
                                                    label="Ad URL"
                                                    initialValue={dataAds.adURL}
                                                >
                                                    <Input placeholder="Enter Ad URL" />
                                                </Form.Item>
                                            </Col>

                                        </Row>
                                        <Row gutter={30}>
                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="avatar"

                                                    initialValue={dataAds.image.url}
                                                >
                                                    <Upload
                                                        name="avatar"
                                                        beforeUpload={() => false}
                                                        className="sDash_upload-basic"
                                                        accept="image/*"
                                                        maxCount={1}
                                                    >
                                                        <span className="sDash_upload-text">Select File</span>
                                                        <Link to="#" className="sDash_upload-browse">
                                                            Browse
                                                        </Link>
                                                    </Upload>
                                                </Form.Item>
                                            </Col>
                                        </Row>

                                        <div className="sDash_agree-check">
                                            <Form.Item name="description" label="Description" rules={[{ min: 10, message: 'must be at least 10 characters!' }]} initialValue={
                                                dataAds.description
                                            }>
                                                <TextArea className="sDash_unresizable" placeholder='Enter Description' />
                                            </Form.Item>
                                        </div>

                                        <div className="sDash_form-action mt-20">
                                            <Button htmlType="submit" type="primary" size="large">
                                                {loading ? "loading..." : "Submit Form"}
                                            </Button>
                                        </div>
                                    </Form>}
                                </Cards>
                            </VerticalFormStyleWrap>
                        </FormValidationWrap>
                    </Col>
                </Row>
            </Main>

        </>
    );
};

export default EditAdvertisement;
