/* eslint-disable no-underscore-dangle */
/* eslint-disable prettier/prettier */
import actions from './actions';

const { GET_LIST_ADVERTISEMENT, DELETE_ADVERTISEMENT, ADD_ADVERTISEMENT, GET_LIST_ADVERTISEMENT_BEGIN, SEARCH_ADVERTISEMENT_BEGIN, SEARCH_ADVERTISEMENT, ERROR_ADVERTISEMENT_SEARCH, GET_ADVERTISEMENT_BY_ID, EDIT_TYPE_ADVERTISEMENT } = actions;

const initialState = {
    listAdvertisement: [],
    totalItems: 0,
    loading: false,
    error: null,
    errorMessage: null,
    notFound: false,
    dataAds: null,
    adTypeSelect: null
};

const advertisementReducer = (state = initialState, action) => {
    const { type, data, id, errorMessageAdd, dataAds, typeAds } = action;
    switch (type) {
        case GET_LIST_ADVERTISEMENT_BEGIN:
            return {
                ...state,
                loading: true,
            };
        case GET_LIST_ADVERTISEMENT:
            return {
                ...state,
                listAdvertisement: data?.docs,
                totalItems: data?.total,
                loading: false,
            };
        case DELETE_ADVERTISEMENT:
            return {
                ...state,
                listAdvertisement: state.listAdvertisement.filter(item => item._id !== id),
            };
        case ADD_ADVERTISEMENT:
            return {
                ...state,
                loading: false,
                errorMessage: errorMessageAdd,
            };
        case SEARCH_ADVERTISEMENT_BEGIN:
            return {
                ...state,
                loading: true,
                notFound: false
            };
        case SEARCH_ADVERTISEMENT:
            return {
                ...state,
                listAdvertisement: data?.docs,
                totalItems: data?.total,
                loading: false,
            };

        case ERROR_ADVERTISEMENT_SEARCH:
            return {
                ...state,
                notFound: true,
                loading: false,
            };

        case GET_ADVERTISEMENT_BY_ID:
            return {
                ...state,
                dataAds,
                adTypeSelect: dataAds.adType
            }

        case EDIT_TYPE_ADVERTISEMENT:
            return {
                ...state,
                adTypeSelect: typeAds
            }

        default:
            return state;
    }
}

export default advertisementReducer;
