/* eslint-disable prettier/prettier */
import actions from './actions';

const { ADD_NEWS, GET_LIST_NEWS_BEGIN, GET_NEWS_ITEM, DELETE_NEWS_ITEM } = actions;

const initialState = {
    newsData: null,
    loading: false,
    errorMessage: null
};
const newsReducer = (state = initialState, action) => {
    const { type, errorMessageAdd, data, itemID } = action;
    switch (type) {
        case GET_LIST_NEWS_BEGIN:
            return {
                ...state,
                loading: true,
            };

        case ADD_NEWS:
            return {
                ...state,
                loading: false,
                errorMessage: errorMessageAdd,
            };
        case GET_NEWS_ITEM:
            return {
                ...state,
                newsData: data
            }
        case DELETE_NEWS_ITEM: {
            return {
                ...state,
                newsData: state.result?.map((element) => {
                    return { ...element, news: element.news.filter(({ _id }) => _id !== itemID) }
                })



            }
        }
        default:
            return state;
    }
};

export default newsReducer;
