/* eslint-disable no-unused-vars */
/* eslint-disable prettier/prettier */
import { Button } from 'antd';
import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';

import { useDispatch } from 'react-redux';
import { PageHeader } from '../../components/page-headers/page-headers';
import { CardToolbox } from '../styled';
import { AutoComplete } from '../../components/autoComplete/autoComplete';
import { getNews } from '../../redux/news/actionCreator';



const ListNews = () => {
    const dispatch = useDispatch();
    
    useEffect(() => {
        dispatch(getNews("All"))
    }, [dispatch])
    return (
        <>
            <CardToolbox>
                <PageHeader
                    ghost
                    title="List News"
                    subTitle={
                        <>
                            <AutoComplete
                                placeholder="Search by Name"
                                width="100%"
                                patterns

                            />
                        </>
                    }
                    buttons={[
                        <Button className="btn-add_new" size="default" type="primary" key="1">
                            <Link to="/admin/AddNews">+ Add News</Link>
                        </Button>
                    ]}
                />
            </CardToolbox>

            {/* <Main>
                <Row gutter={15}>
                    <Col md={24}>
                        {listAdvertisement && listAdvertisement.length > 0 ? (
                            <AdvertisementTable data={listAdvertisement} />
                        ) : null}
                    </Col>
                </Row>
            </Main> */}
        </>
    );
};

export default ListNews;
