/* eslint-disable react/prop-types */
import React, { useState } from 'react';
import { Table } from 'antd';
import FeatherIcon from 'feather-icons-react';
import moment from 'moment';
import AdsModalEdit from './AdsModalEdit';
import { UserTableStyleWrapper } from '../pages/style';
import { TableWrapper } from '../styled';
import Heading from '../../components/heading/heading';
import { Button } from '../../components/buttons/buttons';
import { Cards } from '../../components/cards/frame/cards-frame';

const AdvertisementTable = ({ data }) => {
  const [state, setState] = useState({ visible: false, modalType: 'edit', colorModal: false });

  const showModal = type => {
    setState({
      visible: true,
      modalType: type,
    });
  };

  const usersTableData = [];

  data.docs.map(ad => {
    const { _id, image, title, startDate, endDate, adPosition, count, isVisible } = ad;

    return usersTableData.push({
      key: _id,
      title: (
        <div className="user-info">
          <figure>
            <img style={{ width: '40px' }} src={image} alt="" />
          </figure>
          <figcaption>
            <Heading className="user-name" as="h6">
              {title.length > 12 ? `${title.slice(0, 12)}...` : title}
            </Heading>
          </figcaption>
        </div>
      ),
      startdate: moment(startDate).format('DD/MM/YYYY HH:mm'),
      enddate: moment(endDate).format('DD/MM/YYYY HH:mm'),
      adposition: adPosition,
      count,
      visiblaity: isVisible ? 'Visible' : 'Hidden',
      action: (
        <div className="table-actions">
          <>
            <Button className="btn-icon" onClick={() => showModal('primary')} type="edit" to="#" shape="circle">
              <FeatherIcon icon="eye" size={16} />
            </Button>
            <AdsModalEdit modalType={state.modalType} visible={state.visible} />
            <Button className="btn-icon" type="info" to="#" shape="circle">
              <FeatherIcon icon="edit" size={16} />
            </Button>
            <Button className="btn-icon" type="danger" to="#" shape="circle">
              <FeatherIcon icon="trash-2" size={16} />
            </Button>
          </>
        </div>
      ),
    });
  });

  const adsTableColumns = [
    {
      title: 'Title',
      dataIndex: 'title',
      key: 'title',
    },
    {
      title: 'StartDate',
      dataIndex: 'startdate',
      key: 'startdate',
    },
    {
      title: 'End Date',
      dataIndex: 'enddate',
      key: 'enddate',
    },
    {
      title: 'Ad Position',
      dataIndex: 'adposition',
      key: 'adposition',
    },
    {
      title: 'Count',
      dataIndex: 'count',
      key: 'count',
    },
    {
      title: 'Visiblaity',
      dataIndex: 'visiblaity',
      key: 'visiblaity',
    },
    {
      title: 'Actions',
      dataIndex: 'action',
      key: 'action',
      width: '90px',
    },
  ];

  return (
    <Cards headless>
      <UserTableStyleWrapper>
        <TableWrapper className="table-responsive">
          <Table
            dataSource={usersTableData}
            columns={adsTableColumns}
            pagination={{
              defaultPageSize: 5,
              total: data.docs.length,
              showTotal: (total, range) => `${range[0]} - ${range[1]} of ${total} items`,
            }}
          />
        </TableWrapper>
      </UserTableStyleWrapper>
    </Cards>
  );
};

export default AdvertisementTable;
