/* eslint-disable prettier/prettier */
import { Button, Col, DatePicker, Form, Input, notification, Row, Select, Upload } from 'antd';
import FormData from 'form-data';
import TextArea from 'antd/lib/input/TextArea';
import React, { useEffect } from 'react';
import { Option } from 'antd/lib/mentions';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { FormWrapperNoMargin } from './Style';
import { Cards } from '../../components/cards/frame/cards-frame';
import { PageHeader } from '../../components/page-headers/page-headers';
import { FormValidationWrap, HorizontalFormStyleWrap, VerticalFormStyleWrap } from '../forms/overview/Style';
import { Main } from '../styled';
import { addAdvertisement } from '../../redux/advertisement/actionCreator';

const AddNews = () => {

    return (
        <>
            <PageHeader
                ghost
                title="Add Advertisement"
                buttons={[
                    <Button className="btn-add_new" size="default" type="primary" key="1">
                        <Link to="/admin/listAdvertisement">List Advertisement</Link>
                    </Button>
                ]} />
    
        </>
    );
};

export default AddNews;
