/* eslint-disable prefer-promise-reject-errors */
import React, { useCallback, useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom/cjs/react-router-dom.min';
import { Form, Input, Button, notification } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { AuthWrapper } from './style';
import Heading from '../../../../components/heading/heading';
import { reset } from '../../../../redux/resetPassword/actionCreator';

const ResetPass = () => {
  const dispatch = useDispatch();
  const error = useSelector(state => state.ResetPassword.error);
  const { token } = useParams();
  const [state, setState] = useState({
    value: { password: '', confirm: '' },
  });

  const openNotificationWithIcon = type => {
    console.log('type', type);
    notification[type]({
      message: 'Notification Title',
      description:
        'This is the content of the notification. This is the content of the notification. This is the content of the notification.',
    });
  };
  const handleSubmit = useCallback(values => {
    if (values.password === values.confirm) {
      console.log(error);
      dispatch(reset(values.password, token));
      setState({ ...state, values });
      if (error.status === 'success') {
        openNotificationWithIcon('success');
      } else {
        openNotificationWithIcon('error');
      }
    }
  }, [dispatch, error]);



  return (
    <AuthWrapper>
      <div className="auth-contents">
        <Form name="forgotPass" layout="vertical" onFinish={handleSubmit}>
          <Heading as="h3">Reset Password?</Heading>
          <p className="forgot-text">Reset password in two quick steps</p>
          <Form.Item
            name="password"
            label="Password"
            rules={[
              {
                required: true,
                message: 'Please input your password!',
              },
            ]}
            hasFeedback
          >
            <Input.Password />
          </Form.Item>

          <Form.Item
            name="confirm"
            label="Confirm Password"
            dependencies={['password']}
            hasFeedback
            rules={[
              {
                required: true,
                message: 'Please confirm your password!',
              },
              ({ getFieldValue }) => ({
                validator(rule, value) {
                  if (!value || getFieldValue('password') === value) {
                    return Promise.resolve();
                  }
                  return Promise.reject('The two passwords that you entered do not match!');
                },
              }),
            ]}
          >
            <Input.Password />
          </Form.Item>

          <Form.Item>
            <Button className="btn-reset" htmlType="submit" type="primary" size="large">
              Send Reset Instructions
            </Button>
          </Form.Item>
          <p className="return-text">
            Return to <NavLink to="/">Sign In</NavLink>
          </p>
        </Form>
      </div>
    </AuthWrapper>
  );
};

export default ResetPass;
