              'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYyYWJjNmJhYmQzNDY5MTEzNGIyOGRmMiIsImVtYWlsIjoibm8tYWFzaHV0b3NoQG1vYmlsb2l0dGUuY29tIiwidXNlclR5cGUiOiJBZG1pbiIsImlhdCI6MTY1OTQ0MTg1OCwiZXhwIjoxNjU5NTI4MjU4fQ.U33zdfwkecJa68_gltaG7VdmY0cY9yJ58PrtxV5-36o',
          },
        },
      );
      console.log(response);
    } catch (err) {
      // dispatch(loginErr(err));
    }
  };
};

export { forgetPassword };
