/* eslint-disable react/prop-types */
import React, { useState } from 'react';
import { Empty, Spin, Switch, Table } from 'antd';
import FeatherIcon from 'feather-icons-react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { UserTableStyleWrapper } from '../pages/style';
import { TableWrapper } from '../styled';
import { Button } from '../../components/buttons/buttons';
import { Cards } from '../../components/cards/frame/cards-frame';

import { SpinerWraperStyle } from '../ui-elements/ui-elements-styled';
import { deleteSubAdmin, getListSubAdmin, updateSubAdminStatus } from '../../redux/subAdmin/actionCreator';

const SubAdminTable = ({ data }) => {
  const dispatch = useDispatch();
  const [currentPage, setCurrentPage] = useState(1);
  const { loading, totalItems, notFound } = useSelector(state => state.SubAdmin);

  const usersTableData = [];

  data.map(admin => {
    const { _id, role, email, firstName, lastName, mobileNumber, status } = admin;

    return usersTableData.push({
      key: _id,
      name: <div className="user-info">{`${firstName} ${lastName}`}</div>,
      email,
      phoneNO: mobileNumber,
      role,
      status: (
        <div>
          <Switch
            checkedChildren="Active"
            unCheckedChildren="Block"
            defaultChecked={status === 'ACTIVE'}
            onClick={() => {
              dispatch(updateSubAdminStatus(_id));
            }}
          />
        </div>
      ),
      action: (
        <div className="table-actions">
          <>
            <Button className="btn-icon" type="view" shape="circle">
              <FeatherIcon icon="eye" size={16} />
            </Button>

            <Link className="btn-icon" type="info" shape="circle" to={`/admin/editSubAdmin/${_id}`} data={admin}>
              <FeatherIcon icon="edit" size={16} style={{ color: '#ADB4D2' }} />
            </Link>
            <Button
              className="btn-icon"
              type="danger"
              to="#"
              shape="circle"
              onClick={() => {
                dispatch(deleteSubAdmin(_id));
              }}
            >
              <FeatherIcon icon="trash-2" size={16} />
            </Button>
          </>
        </div>
      ),
    });
  });

  const subAdminTableColumns = [
    {
      title: 'Name',
      dataIndex: 'name',
      key: 'name',
    },
    {
      title: 'E-mail',
      dataIndex: 'email',
      key: 'email',
    },

    {
      title: 'Phone Number',
      dataIndex: 'phoneNO',
      key: 'phoneNO',
    },
    {
      title: 'Role',
      dataIndex: 'role',
      key: 'role',
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
    },
    {
      title: 'Actions',
      dataIndex: 'action',
      key: 'action',
      width: '90px',
    },
  ];

  return (
    <Cards headless>
      <UserTableStyleWrapper>
        <TableWrapper className="table-responsive">
          {loading ? (
            <SpinerWraperStyle>
              <Spin />
            </SpinerWraperStyle>
          ) : !notFound ? (
            <Table
              dataSource={usersTableData}
              columns={subAdminTableColumns}
              pagination={{
                defaultPageSize: 5,
                total: totalItems,
                showTotal: (total, range) => `${range[0]} - ${range[1]} of ${totalItems} items`,
                onChange: (page, pageSize) => {
                  dispatch(getListSubAdmin(page, pageSize));
                  setCurrentPage(page);
                },
                current: currentPage,
              }}
            />
          ) : (
            <Cards title="Basic" caption="The simplest use of Empty">
              <Empty />
            </Cards>
          )}
        </TableWrapper>
      </UserTableStyleWrapper>
    </Cards>
  );
};

export default SubAdminTable;
