const actions = {
  GET_LIST_ADVERTISEMENT: 'GET_LIST_ADVERTISEMENT',
  DELETE_ADVERTISEMENT: 'DELETE_ADVERTISEMENT',
  ADD_ADVERTISEMENT: 'ADD_ADVERTISEMENT',
  getAdvertisementBegin: 'getAdvertisementBegin',
  
  getAdvertisement: data => {
    return {
      type: actions.GET_LIST_ADVERTISEMENT,
      data,
    };
  },
  deleteAds: id => {
    return {
      type: actions.DELETE_ADVERTISEMENT,
      id,
    };
  },
  addAds: errorMessageAdd => {
    return {
      type: actions.ADD_ADVERTISEMENT,
      errorMessageAdd,
    };
  },
};

export default actions;
