/* eslint-disable prettier/prettier */
import { Button, Col, DatePicker, Form, Input, notification, Row, Select, Upload } from 'antd';
import FormData from 'form-data';
import TextArea from 'antd/lib/input/TextArea';
import React, { useEffect } from 'react';
import { Option } from 'antd/lib/mentions';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { FormWrapperNoMargin } from './Style';
import { Cards } from '../../components/cards/frame/cards-frame';
import { PageHeader } from '../../components/page-headers/page-headers';
import { FormValidationWrap, HorizontalFormStyleWrap, VerticalFormStyleWrap } from '../forms/overview/Style';
import { Main } from '../styled';
import { addAdvertisement } from '../../redux/advertisement/actionCreator';

const AddSubAdmin = () => {
    const { errorMessage, loading } = useSelector(state => state.Advertisement);
    const dispatch = useDispatch();
    const [click, setClick] = React.useState(false);

    const formDataDate = new FormData();
    const formDataImpression = new FormData();
    const formDataClick = new FormData();

    const [form] = Form.useForm();

    const openNotificationWithIcon = type => {
        notification[type]({
            message: 'Notification',
            description: errorMessage.responseMessage
        });
    };



    const getvalue = async (value) => {
        if (click === 'Date') {
            formDataDate.append('title', value.title);
            formDataDate.append('description', value.description);
            formDataDate.append('startDate', value.startdate);
            formDataDate.append('endDate', value.enddate);
            formDataDate.append('image', value.avatar.file);
            formDataDate.append('adPosition', value.positionSelect);
            formDataDate.append('adType', value.typeSelect);
            formDataDate.append('viewMode', value.viewMode);
            formDataDate.append('adURL', value.AdURL);

            dispatch(addAdvertisement(formDataDate));

            // remove form data after submit
            formDataDate.delete('title');
            formDataDate.delete('description');
            formDataDate.delete('startDate');
            formDataDate.delete('endDate');
            formDataDate.delete('image');
            formDataDate.delete('adPosition');
            formDataDate.delete('adType');
            formDataDate.delete('viewMode');
            formDataDate.delete('adURL');
        }
        if (click === 'Impression') {
            formDataImpression.append('title', value.title);
            formDataImpression.append('description', value.description);
            formDataImpression.append('impressionLimit', value.impressionLimit);
            formDataImpression.append('image', value.avatar.file);
            formDataImpression.append('adPosition', value.positionSelect);
            formDataImpression.append('adType', value.typeSelect);
            formDataImpression.append('viewMode', value.viewMode);
            formDataImpression.append('adURL', value.AdURL);
            dispatch(addAdvertisement(formDataImpression));

            // remove form data after submit
            formDataImpression.delete('title');
            formDataImpression.delete('description');
            formDataImpression.delete('impressionLimit');
            formDataImpression.delete('image');
            formDataImpression.delete('adPosition');
            formDataImpression.delete('adType');
            formDataImpression.delete('viewMode');
            formDataImpression.delete('adURL');
        }
        if (click === 'Click') {
            formDataClick.append('title', value.title);
            formDataClick.append('description', value.description);
            formDataClick.append('clickLimit', value.clickLimit);
            formDataClick.append('image', value.avatar.file);
            formDataClick.append('adPosition', value.positionSelect);
            formDataClick.append('adType', value.typeSelect);
            formDataClick.append('viewMode', value.viewMode);
            formDataClick.append('adURL', value.AdURL);
            dispatch(addAdvertisement(formDataClick));

            // remove form data after submit
            formDataClick.delete('title');
            formDataClick.delete('description');
            formDataClick.delete('clickLimit');
            formDataClick.delete('image');
            formDataClick.delete('adPosition');
            formDataClick.delete('adType');
            formDataClick.delete('viewMode');
            formDataClick.delete('adURL');
        }

    }


    useEffect(() => {
        if (errorMessage) {

            if (errorMessage?.responseCode === 200) {
                openNotificationWithIcon('success');
            }
            else {
                openNotificationWithIcon('error');
            }
        }
    }, [errorMessage]);
    return (
        <>
            <PageHeader
                ghost
                title="Add Advertisement"
                buttons={[
                    <Button className="btn-add_new" size="default" type="primary" key="1">
                        <Link to="/admin/listAdvertisement">List Advertisement</Link>
                    </Button>
                ]} />
        </>
    );
};

export default AddSubAdmin;
