import axios from 'axios';
import Cookies from 'js-cookie';
import actions from './actions';

const { addNewSubAdmin } = actions;

const addSubAdmin = data => {
  return async dispatch => {
    try {
      dispatch(getListSubAdminBegin());

      const response = await axios.post('http://20.218.124.106:1885/api/v1/admin/addSubAdmin', data, {
        headers: {
          'Content-Type': 'application/json',
          token: Cookies.get('token'),
        },
      });
      if (response.status === 200) dispatch(addNewSubAdmin(response.data));
    } catch (err) {
      dispatch(addNewSubAdmin(err.response.data));
    }
  };
};

export { addSubAdmin };
