/* eslint-disable prettier/prettier */
import React, { useEffect, useState } from 'react';
import { Button, Col, Form, Input, notification, Row, Select, Upload } from 'antd';
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom/cjs/react-router-dom.min';
import PhoneInput, { getCountryCallingCode } from 'react-phone-number-input'
import { Option } from 'antd/lib/mentions';
import { useDispatch, useSelector } from 'react-redux';
import { FormValidationWrap, VerticalFormStyleWrap } from '../forms/overview/Style';
import { Cards } from '../../components/cards/frame/cards-frame';
import { PageHeader } from '../../components/page-headers/page-headers';
import { Checkbox } from '../../components/checkbox/checkbox';
import 'react-phone-number-input/style.css'
import { Main } from '../styled';
import { getSubAdminById } from '../../redux/subAdmin/actionCreator';



const EditSubAdmin = () => {
    const { SubAdminId } = useParams();
    const dispatch = useDispatch();
    const { dataAdmin } = useSelector(state => state.SubAdmin);
    const [form] = Form.useForm();

    const [permission, setPermission] = useState({
        kycManagement: {
            "isRead": false,
            "isWrite": false,
            "isUpdate": false,
            "isDelete": true
        },
        tokenManagement: {
            "isRead": false,
            "isWrite": false,
            "isUpdate": false,
            "isDelete": false
        },
        userManagement: {
            "isRead": false,
            "isWrite": false,
            "isUpdate": false,
            "isDelete": false
        },
        subadminManagement: {
            "isRead": false,
            "isWrite": false,
            "isUpdate": false,
            "isDelete": false
        },
        susbsciptionManagement: {
            "isRead": false,
            "isWrite": false,
            "isUpdate": false,
            "isDelete": false
        },
        contractSnifferManagement: {
            "isRead": false,
            "isWrite": false,
            "isUpdate": false,
            "isDelete": false
        },
        advertisementManagement: {
            "isRead": false,
            "isWrite": false,
            "isUpdate": false,
            "isDelete": false
        },
        staticContentManagement: {
            "isRead": false,
            "isWrite": true,
            "isUpdate": false,
            "isDelete": true
        },
        helpCenterManagement: {
            "isRead": false,
            "isWrite": false,
            "isUpdate": false,
            "isDelete": false
        },
        announcementManagement: {
            "isRead": false,
            "isWrite": false,
            "isUpdate": false,
            "isDelete": false
        },
        blogManagement: {
            "isRead": false,
            "isWrite": false,
            "isUpdate": false,
            "isDelete": false
        },
        newsManagement: {
            "isRead": false,
            "isWrite": false,
            "isUpdate": false,
            "isDelete": false
        },
        sponsorManagement: {
            "isRead": false,
            "isWrite": false,
            "isUpdate": false,
            "isDelete": false
        }
    })

    const [NOPhone, setNOPhone] = useState()

    useEffect(() => {
        dispatch(getSubAdminById(SubAdminId));
    }, [dispatch, SubAdminId]);
    if (dataAdmin && NOPhone == null) {
        const phone = `+${getCountryCallingCode(dataAdmin?.countryCode)}${dataAdmin.mobileNumber}`
        setNOPhone(phone)
    }
    const getValues = values => {
        console.log(values, NOPhone)
    }

    return (
        <>
            <PageHeader
                ghost
                title="Edit Sub Admin"
                buttons={[
                    <Button className="btn-add_new" size="default" type="primary" key="1" >
                        <Link to="/admin/listAdvertisement">Edit Sub Admin</Link>
                    </Button>
                ]} />

            <Main>
                <Row gutter={25}>
                    <Col xs={24}>
                        <FormValidationWrap>
                            <VerticalFormStyleWrap>
                                <Cards >
                                    {dataAdmin && <Form name="sDash_validation-form" form={form} layout="vertical" onFinish={getValues}>
                                        <Row gutter={30}>
                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="fName"
                                                    label="First Name"
                                                    initialValue={dataAdmin.firstName}
                                                    rules={[
                                                        { required: true, message: 'First Name required!' },
                                                        { min: 10, message: 'must be at least 10 characters!' }
                                                    ]}
                                                >
                                                    <Input placeholder="Enter First Name" />
                                                </Form.Item>
                                            </Col>

                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="lName"
                                                    label="Last Name"
                                                    initialValue={dataAdmin.lastName}
                                                    rules={[
                                                        { required: true, message: 'Last Name required!' },
                                                        { min: 10, message: 'must be at least 10 characters!' }
                                                    ]}
                                                >
                                                    <Input placeholder="Enter Last Name" />
                                                </Form.Item>
                                            </Col>

                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="email"
                                                    label="E-mail"
                                                    initialValue={dataAdmin.email}
                                                    rules={[
                                                        { required: true, message: 'E-mail required!' },
                                                        { type: 'email', message: 'The input is not valid E-mail!', }
                                                    ]}
                                                >
                                                    <Input placeholder="Enter title" />
                                                </Form.Item>
                                            </Col>


                                            <Col md={8} xs={24}>
                                                <Form.Item name="PhoneNO"
                                                    label="phone number">
                                                    <div className='hhh'>
                                                        <PhoneInput
                                                            placeholder="Enter phone number"
                                                            value={NOPhone}
                                                            defaultCountry={dataAdmin?.countryCode}
                                                            onChange={setNOPhone} />
                                                    </div>
                                                </Form.Item>
                                            </Col>

                                            <Col md={8} xs={24}>
                                                <Form.Item name="gender" label="Gender" rules={[{ required: true, message: 'Gender required!' }]} initialValue={dataAdmin.gender}>
                                                    <Select size="large" className="sDash_fullwidth-select" placeholder="Select Gender" >
                                                        <Option value="Male" >Male</Option>
                                                        <Option value="Female">Female</Option>
                                                    </Select>
                                                </Form.Item>
                                            </Col>


                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="profilePic"
                                                    label="picture"
                                                    initialValue={dataAdmin.profilePic.url}
                                                    rules={[{ required: true, message: 'profile picture required!' }]}
                                                >
                                                    <Upload
                                                        name="profilePic"
                                                        beforeUpload={() => false}
                                                        className="sDash_upload-basic"
                                                        accept="image/*"
                                                        maxCount={1}
                                                    >
                                                        <span className="sDash_upload-text">Select picture</span>
                                                        <Link to="#" className="sDash_upload-browse">
                                                            Browse
                                                        </Link>
                                                    </Upload>
                                                </Form.Item>
                                            </Col>
                                        </Row>

                                        <Row gutter={30}>
                                            <Col md={8} xs={24} style={{
                                                color: "#272B41", fontWeight: 500, fontSize: "16px"
                                            }}>
                                                Snippet
                                            </Col>
                                            <Col md={4} xs={24} style={{
                                                textAlign: 'center',
                                                color: "#272B41", fontWeight: 500, fontSize: "16px"
                                            }}>
                                                Write
                                            </Col>
                                            <Col md={4} xs={24} style={{
                                                textAlign: 'center',
                                                color: "#272B41", fontWeight: 500, fontSize: "16px"
                                            }}>
                                                Read
                                            </Col>
                                            <Col md={4} xs={24} style={{
                                                textAlign: 'center',
                                                color: "#272B41", fontWeight: 500, fontSize: "16px"
                                            }}>
                                                Update
                                            </Col>
                                            <Col md={4} xs={24} style={{
                                                textAlign: 'center',
                                                color: "#272B41", fontWeight: 500, fontSize: "16px"
                                            }}>
                                                Delete
                                            </Col>
                                        </Row>
                                        <Row gutter={30} style={{ marginTop: "15px" }}>
                                            <Col md={8} xs={24} style={{
                                                color: "#272B41", fontWeight: 500, fontSize: "16px"
                                            }}>
                                                KYC Management
                                            </Col>
                                            <Col md={4} xs={24} style={{ textAlign: 'center' }}>
                                                <Checkbox checked={permission.kycManagement.isWrite} />
                                            </Col>
                                            <Col md={4} xs={24} style={{ textAlign: 'center' }}>
                                                <Checkbox checked={permission.kycManagement.isRead} />
                                            </Col>
                                            <Col md={4} xs={24} style={{ textAlign: 'center' }}>
                                                <Checkbox checked={permission.kycManagement.isUpdate} />
                                            </Col>
                                            <Col md={4} xs={24} style={{ textAlign: 'center' }}>
                                                <Checkbox checked={permission.kycManagement.isDelete} />
                                            </Col>
                                        </Row>

                                        <div className="sDash_form-action mt-20">
                                            <Button htmlType="submit" type="primary" size="large">
                                                Submit Form
                                            </Button>
                                        </div>
                                    </Form>}
                                </Cards>
                            </VerticalFormStyleWrap>
                        </FormValidationWrap>
                    </Col>
                </Row>
            </Main>


        </>
    );
};

export default EditSubAdmin;
