/* eslint-disable prettier/prettier */
import React, { useState } from 'react';
import { Button, Col, DatePicker, Form, Input, notification, Row, Select, Upload } from 'antd';
import PhoneInput, { isValidPhoneNumber } from 'react-phone-number-input'
import { Option } from 'antd/lib/mentions';
import { Link } from 'react-router-dom';
import TextArea from 'antd/lib/input/TextArea';
import { Main } from '../styled';
import { PageHeader } from '../../components/page-headers/page-headers';
import { FormValidationWrap, VerticalFormStyleWrap } from '../forms/overview/Style';
import { Cards } from '../../components/cards/frame/cards-frame';

import 'react-phone-number-input/style.css'


const AddSubAdmin = () => {
    const [form] = Form.useForm();
    const [value, setValue] = useState()
    const getValues = (values) => {
    }

    const handleValidate = (value) => {
        const isValid = isValidPhoneNumber(value);
        console.log({ isValid })
        return isValid
    }


    return (
        <>
            <PageHeader
                ghost
                title="Add Sub Admin"
                buttons={[
                    <Button className="btn-add_new" size="default" type="primary" key="1">
                        {/* <Link to="/admin/listAdvertisement">List Advertisement</Link> */}
                    </Button>
                ]} />

            <Main>
                <Row gutter={25}>
                    <Col xs={24}>
                        <FormValidationWrap>
                            <VerticalFormStyleWrap>
                                <Cards >
                                    <Form name="sDash_validation-form" form={form} layout="vertical" onFinish={getValues}>
                                        <Row gutter={30}>
                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="fName"
                                                    label="First Name"
                                                    rules={[
                                                        { required: true, message: 'First Name required!' },
                                                        { min: 10, message: 'must be at least 10 characters!' }
                                                    ]}
                                                >
                                                    <Input placeholder="Enter First Name" />
                                                </Form.Item>
                                            </Col>

                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="lName"
                                                    label="Last Name"
                                                    rules={[
                                                        { required: true, message: 'Last Name required!' },
                                                        { min: 10, message: 'must be at least 10 characters!' }
                                                    ]}
                                                >
                                                    <Input placeholder="Enter Last Name" />
                                                </Form.Item>
                                            </Col>

                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="email"
                                                    label="E-mail"
                                                    rules={[
                                                        { required: true, message: 'E-mail required!' },
                                                        { type: 'email', message: 'The input is not valid E-mail!', }
                                                    ]}
                                                >
                                                    <Input placeholder="Enter title" />
                                                </Form.Item>
                                            </Col>




                                            <Col md={8} xs={24}>
                                                <Form.Item name="enddate"
                                                
                                                    label="End Date">
                                                    <div className='hhh'>
                                                        <PhoneInput
                                                            placeholder="Enter phone number"
                                                            value={value}
                                                            defaultCountry="US"
                                                            onChange={setValue} />
                                                    </div>
                                                </Form.Item>
                                            </Col>



                                        </Row>




                                        <div className="sDash_form-action mt-20">
                                            <Button htmlType="submit" type="primary" size="large">
                                                Submit Form
                                            </Button>
                                        </div>
                                    </Form>
                                </Cards>
                            </VerticalFormStyleWrap>
                        </FormValidationWrap>
                    </Col>
                </Row>
            </Main>
        </>
    );
};

export default AddSubAdmin;
