import axios from 'axios';
import Cookies from 'js-cookie';
import actions from './actions';

const {} = actions;

const addewNews = data => {
  return async dispatch => {
    try {
      const response = await axios.post('http://20.218.124.106:1885/api/v1/admin/addNews', data, {
        headers: {
          'Content-Type': 'application/json',
          token: Cookies.get('token'),
        },
      });

      if (response.status === 200) dispatch(addNews(response.data));
    } catch (err) {
      dispatch(addNews(err.response.data));
    }
  };
};
