/* eslint-disable prettier/prettier */
import { Button, Col, DatePicker, Form, Input, notification, Row, Select, Upload } from 'antd';
import FormData from 'form-data';
import TextArea from 'antd/lib/input/TextArea';
import React, { useEffect } from 'react';
import { Option } from 'antd/lib/mentions';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { FormWrapperNoMargin } from './Style';
import { Cards } from '../../components/cards/frame/cards-frame';
import { PageHeader } from '../../components/page-headers/page-headers';
import { FormValidationWrap, HorizontalFormStyleWrap, VerticalFormStyleWrap } from '../forms/overview/Style';
import { Main } from '../styled';
import { addAdvertisement } from '../../redux/advertisement/actionCreator';

const AddAdvertisement = () => {
    const { errorMessage, loading } = useSelector(state => state.Advertisement);
    const dispatch = useDispatch();
    const [click, setClick] = React.useState(false);

    const formDataDate = new FormData();
    const formDataImpression = new FormData();
    const formDataClick = new FormData();

    const [form] = Form.useForm();

    const openNotificationWithIcon = type => {
        notification[type]({
            message: 'Notification',
            description: errorMessage.responseMessage
        });
    };



    const getvalue = async (value) => {
        if (click === 'Date') {
            formDataDate.append('title', value.title);
            formDataDate.append('description', value.description);
            formDataDate.append('startDate', value.startdate);
            formDataDate.append('endDate', value.enddate);
            formDataDate.append('image', value.avatar.file);
            formDataDate.append('adPosition', value.positionSelect);
            formDataDate.append('adType', value.typeSelect);
            formDataDate.append('adURL', value.AdURL);
            dispatch(addAdvertisement(formDataDate));

            // remove form data after submit
            formDataDate.delete('title');
            formDataDate.delete('description');
            formDataDate.delete('startDate');
            formDataDate.delete('endDate');
            formDataDate.delete('image');
            formDataDate.delete('adPosition');
            formDataDate.delete('adType');
            formDataDate.delete('adURL');
        }
        if (click === 'Impression') {
            formDataImpression.append('title', value.title);
            formDataImpression.append('description', value.description);
            formDataImpression.append('impressionLimit', value.impressionLimit);
            formDataImpression.append('image', value.avatar.file);
            formDataImpression.append('adPosition', value.positionSelect);
            formDataImpression.append('adType', value.typeSelect);
            formDataImpression.append('adURL', value.AdURL);
            dispatch(addAdvertisement(formDataImpression));

            // remove form data after submit
            formDataImpression.delete('title');
            formDataImpression.delete('description');
            formDataImpression.delete('impressionLimit');
            formDataImpression.delete('image');
            formDataImpression.delete('adPosition');
            formDataImpression.delete('adType');
            formDataImpression.delete('adURL');
        }
        if (click === 'Click') {
            formDataClick.append('title', value.title);
            formDataClick.append('description', value.description);
            formDataClick.append('clickLimit', value.clickLimit);
            formDataClick.append('image', value.avatar.file);
            formDataClick.append('adPosition', value.positionSelect);
            formDataClick.append('adType', value.typeSelect);
            formDataClick.append('adURL', value.AdURL);
            dispatch(addAdvertisement(formDataClick));

        }

    }


    useEffect(() => {
        if (errorMessage) {

            if (errorMessage?.responseCode === 200) {
                openNotificationWithIcon('success');
            }
            else {
                openNotificationWithIcon('error');
            }
        }
    }, [errorMessage]);
    return (
        <>
            <PageHeader
                ghost
                title="Add Advertisement"
                buttons={[
                    <Button className="btn-add_new" size="default" type="primary" key="1">
                        <Link to="/admin/listAdvertisement">List Advertisement</Link>
                    </Button>
                ]} />
            <Main>
                <Row gutter={25}>
                    <Col xs={24}>
                        <FormValidationWrap>
                            <VerticalFormStyleWrap>
                                <Cards title="Custom Styles">
                                    <Form name="sDash_validation-form" form={form} layout="vertical" onFinish={getvalue}>
                                        <Row gutter={30}>
                                            <Col md={8} xs={24}>
                                                <Form.Item name="typeSelect" label="Ad Type" rules={[{ required: true, message: 'Ad Type required!' }]}>
                                                    <Select size="large" className="sDash_fullwidth-select" placeholder="Select Type"
                                                        onChange={(value) => {
                                                            setClick(value);
                                                        }}
                                                    >
                                                        <Option value="Date" >Date</Option>
                                                        <Option value="Impression">Impression</Option>
                                                        <Option value="Click">Click</Option>
                                                    </Select>
                                                </Form.Item>
                                            </Col>

                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="title"
                                                    label="Title"
                                                    rules={[
                                                        { required: true, message: 'First Name required!' },
                                                        { min: 10, message: 'must be at least 10 characters!' }
                                                    ]}
                                                >
                                                    <Input placeholder="Enter title" />
                                                </Form.Item>
                                            </Col>
                                            {click === "Date" &&
                                                <>
                                                    <Col md={8} xs={24}>
                                                        <FormWrapperNoMargin>
                                                            <HorizontalFormStyleWrap className="sDash_input-form">
                                                                <Form.Item name="startdate"
                                                                    label="Start Date"
                                                                    rules={[{ required: true, message: 'Start Date required!' }]}>
                                                                    <DatePicker placeholder='Start Date' />
                                                                </Form.Item>
                                                            </HorizontalFormStyleWrap>
                                                        </FormWrapperNoMargin>
                                                    </Col>


                                                    <Col md={8} xs={24}>
                                                        <FormWrapperNoMargin>
                                                            <HorizontalFormStyleWrap className="sDash_input-form">
                                                                <Form.Item name="enddate"
                                                                    label="End Date"
                                                                    rules={[{ required: true, message: 'End Date required!' }]}>
                                                                    <DatePicker placeholder='End Date' />
                                                                </Form.Item>
                                                            </HorizontalFormStyleWrap>
                                                        </FormWrapperNoMargin>
                                                    </Col> </>}
                                            {click === "Impression" &&
                                                <Col md={8} xs={24}>
                                                    <Form.Item
                                                        name="impressionLimit"
                                                        label="Impression Limit"
                                                        rules={[
                                                            { required: true, message: 'Impression Limit required!' },
                                                            { pattern: /^[0-9]*$/, message: 'must be a number!' },
                                                        ]}
                                                    >
                                                        <Input placeholder="Impression Limit" />
                                                    </Form.Item>
                                                </Col>}

                                            {click === "Click" && <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="clickLimit"
                                                    label="Click Limit"
                                                    rules={[
                                                        { required: true, message: 'click limit required!' },
                                                        { pattern: /^[0-9]*$/, message: 'must be a number!' },
                                                    ]}
                                                >
                                                    <Input placeholder="Enter Click Limit" />
                                                </Form.Item>
                                            </Col>}



                                            <Col md={8} xs={24}>
                                                <Form.Item name="positionSelect" label="Ad Position" rules={[{ required: true, message: 'Ad Position required!' }]}>
                                                    <Select size="large" className="sDash_fullwidth-select" placeholder="Select Position" >
                                                        <Option value="Home" >Home</Option>
                                                        <Option value="Token">Token</Option>
                                                    </Select>
                                                </Form.Item>
                                            </Col>



                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="AdURL"
                                                    label="Ad URL"
                                                    rules={[{ required: true, message: 'Ad URL required!' },
                                                    ]}>
                                                    <Input placeholder="Enter Ad URL" />
                                                </Form.Item>
                                            </Col>

                                        </Row>
                                        <Row gutter={30}>
                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="avatar"
                                                    rules={[{ required: true, message: 'Ad Image required!' }]}
                                                >
                                                    <Upload
                                                        name="avatar"
                                                        beforeUpload={() => false}
                                                        className="sDash_upload-basic"
                                                        accept="image/*"
                                                        maxCount={1}
                                                    >
                                                        <span className="sDash_upload-text">Select File</span>
                                                        <Link to="#" className="sDash_upload-browse">
                                                            Browse
                                                        </Link>
                                                    </Upload>
                                                </Form.Item>
                                            </Col>
                                        </Row>

                                        <div className="sDash_agree-check">
                                            <Form.Item name="description" label="Description" rules={[{ required: true, message: 'Description required!' }, { min: 10, message: 'must be at least 10 characters!' }]}>
                                                <TextArea className="sDash_unresizable" placeholder='Enter Description' />
                                            </Form.Item>
                                        </div>

                                        <div className="sDash_form-action mt-20">
                                            <Button htmlType="submit" type="primary" size="large">
                                                {loading ? "loading..." : "Submit Form"}
                                            </Button>
                                        </div>
                                    </Form>
                                </Cards>
                            </VerticalFormStyleWrap>
                        </FormValidationWrap>
                    </Col>
                </Row>
            </Main>
        </>
    );
};

export default AddAdvertisement;
