/* eslint-disable consistent-return */
import Cookies from 'js-cookie';
import axios from 'axios';
import actions from './actions';

const { loginBegin, loginSuccess, loginErr, logoutBegin, logoutSuccess, logoutErr } = actions;

const login = ({ username, password }) => {
  return async dispatch => {
    try {
      dispatch(loginBegin());
      // send request to server to login and get token
      const response = await axios.post('https://api.develocity.finance/api/v1/admin/login', {
        email: username,
        password,
      });
      console.log(response);

      if (response.status === 200) {
        Cookies.set('logedIn', true);
        Cookies.set('token', response.data.result.token);
        return dispatch(loginSuccess(true));
      }
    } catch (err) {
      dispatch(loginErr(err.response.data.responseMessage));
    }
  };
};

const logOut = () => {
  return async dispatch => {
    try {
      dispatch(logoutBegin());
      Cookies.remove('logedIn');
      dispatch(logoutSuccess(null));
    } catch (err) {
      dispatch(logoutErr(err));
    }
  };
};

export { login, logOut };
