import axios from "axios";
import actions from "../authentication/actions";

const { getAdvertisement } = actions;
const getListAdvertisement = () => {
    return async dispatch => {
        try {
            const response = await axios.get(
                "https://api.develocity.finance/api/v1/admin/advertisement"
            );
            dispatch(getListAdvertisement(response.data.result));
        } catch (err) {
            console.log(err);
        }
    }
}
