/* eslint-disable no-underscore-dangle */
/* eslint-disable prettier/prettier */
import axios from 'axios';
import Cookies from 'js-cookie';
import actions from './actions';

const { getAdvertisement, deleteAds, addAds } = actions;

// get list data of advertisement
const getListAdvertisement = (pages, limted) => {
    return async dispatch => {
        try {
            dispatch(getListAdvertisementBegin());
            const response = await axios.get(`https://api.develocity.finance/api/v1/admin/advertisementList?page=${pages}&limit=${limted}`,
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'token': Cookies.get('token')
                    }
                });
            dispatch(getAdvertisement(response.data.result));
        } catch (err) {
            console.log(err);
        }
    };
};

// delete item advertisement
const deleteAdvertisement = (id) => {
    return async dispatch => {
        try {
            const response = await axios.delete(`https://api.develocity.finance/api/v1/admin/deleteAdvertisment/${id}`,
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'token': Cookies.get('token')
                    }
                });
            dispatch(deleteAds(response.data.result.deleted._id));
        } catch (err) {
            console.log(err);
        }
    };
}


// add advertisement
const addAdvertisement = (data) => {
    return async dispatch => {
        try {
            const response = await axios.post('https://api.develocity.finance/api/v1/admin/addAdvertisment', data,
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'token': Cookies.get('token')
                    }
                });

            dispatch(addAds(response.data));
        } catch (err) {
            dispatch(addAds(err.response.data));

        }
    };
}






export { getListAdvertisement, deleteAdvertisement, addAdvertisement };
