/* eslint-disable prettier/prettier */
import actions from './actions';

const { GET_LIST_SUB_ADMIN_BEGIN, ADD_NEW_SUB_ADMIN, GET_LIST_SUB_ADMIN, DELETE_ADMIN, ERROR_GET_SUB_ADMIN, SEARCH_ADMIN, ERROR_SUB_ADMIN_SEARCH } = actions;

const initialState = {
    loading: false,
    errorMessage: null,
    listSubAdmin: [],
    totalItems: 0,
    errorMessageGetAdmin: null,
    notFound: false,

};
const subAdminReducer = (state = initialState, action) => {
    const { type, errorMessageAdd, data, AdminId, errorMessageGetAdmin, dataSearch } = action;
    switch (type) {
        case GET_LIST_SUB_ADMIN_BEGIN:
            return {
                ...state,
                loading: true,
            };

        case ADD_NEW_SUB_ADMIN:
            return {
                ...state,
                loading: false,
                errorMessage: errorMessageAdd,
            };

        case GET_LIST_SUB_ADMIN:
            return {
                ...state,
                loading: false,
                listSubAdmin: data?.docs,
                totalItems: data?.total,
            }

        case DELETE_ADMIN:
            return {
                ...state,
                listSubAdmin: state.listSubAdmin.filter(({ _id }) => _id !== AdminId),
            };
        case ERROR_GET_SUB_ADMIN:
            return {
                ...state,
                errorMessageGetAdmin
            }

        case ERROR_SUB_ADMIN_SEARCH:
            return {
                ...state,
                notFound: true,
                loading: false,
            };

        case SEARCH_ADMIN: {
            return {
                ...state,
                listSubAdmin: dataSearch
            }
        }
        default:
            return state;
    }
};

export default subAdminReducer;
