/* eslint-disable no-unused-vars */
/* eslint-disable prettier/prettier */
import { Button, Col, Empty, Row } from 'antd';
import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';
import AdvertisementTable from './AdvertisementTable';
import { PageHeader } from '../../components/page-headers/page-headers';
import { CardToolbox, Main } from '../styled';
import { AutoComplete } from '../../components/autoComplete/autoComplete';
import { getListAdvertisement, loadingStart, serchAdvertisement } from '../../redux/advertisement/actionCreator';
import { Cards } from '../../components/cards/frame/cards-frame';



const ListAdvertisement = () => {
    const { listAdvertisement } = useSelector(state => state.Advertisement);

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getListAdvertisement(1, 5));
    }, [dispatch]);


    return (
        <>
            <CardToolbox>
                <PageHeader
                    ghost
                    title="List Advertisement"
                    subTitle={
                        <>
                            <AutoComplete
                                listAdvertisement={listAdvertisement}
                                placeholder="Search by Name"
                                width="100%"
                                onkeyup={value => {
                                    dispatch(loadingStart())
                                    value.length !== 0 ? dispatch(serchAdvertisement(value)) : dispatch(getListAdvertisement(1, 5))
                                }}

                                patterns

                            />
                        </>
                    }
                    buttons={[
                        <Button className="btn-add_new" size="default" type="primary" key="1">
                            <Link to="/admin/addAdvertisement">+ Add New Advertisement</Link>
                        </Button>
                    ]}
                />
            </CardToolbox>

            <Main>
                <Row gutter={15}>
                    <Col md={24}>
                        {listAdvertisement && listAdvertisement.length > 0 ? (
                            <AdvertisementTable data={listAdvertisement} />
                        ) : <Cards Cards caption="The simplest use of Empty" title="NoData">
                            <Empty />
                        </Cards>}
                    </Col>
                </Row>
            </Main>
        </>
    );
};

export default ListAdvertisement;
