/* eslint-disable no-console */
import Cookies from 'js-cookie';
import axios from 'axios';
import actions from './actions';

const { loginBegin, loginSuccess, loginErr, logoutBegin, logoutSuccess, logoutErr } = actions;

const login = ({ username, password }) => {
  return async dispatch => {
    try {
      // async call to server and wait for response
      const response = await axios.post('https://api.develocity.finance/api/v1/admin/login', { email: username, password });

      console.log(response);
      // if response is successfully received
      if (response.status === 200) {
        // set cookie with token
        Cookies.set('token', response.data.token);
        // dispatch action to update state
        dispatch(loginSuccess(response.data.token));
      }

      // fetch('https://api.develocity.finance/api/v1/admin/login', {
      //   method: 'POST',
      //   body: JSON.stringify({ email: username, password }),
      //   headers: {
      //     'Content-Type': 'application/json',
      //   },
      // })
      //   .then(res => res.json())
      //   .then(res => console.log(res));

      // dispatch(loginBegin());
      // setTimeout(() => {
      //   Cookies.set('logedIn', true);
      //   return dispatch(loginSuccess(true));
      // }, 1000);
    } catch (err) {
      dispatch(loginErr(err));
    }
  };
};

const logOut = () => {
  return async dispatch => {
    try {
      dispatch(logoutBegin());
      Cookies.remove('logedIn');
      dispatch(logoutSuccess(null));
    } catch (err) {
      dispatch(logoutErr(err));
    }
  };
};

export { login, logOut };
