/* eslint-disable no-unused-vars */
/* eslint-disable prettier/prettier */
import { Button, Col, Row } from 'antd';
import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';
import AdvertisementTable from './AdvertisementTable';
import { PageHeader } from '../../components/page-headers/page-headers';
import { CardToolbox, Main } from '../styled';
import { AutoComplete } from '../../components/autoComplete/autoComplete';
import { getListAdvertisement } from '../../redux/advertisement/actionCreator';



const ListAdvertisement = () => {
    const { listAdvertisement } = useSelector(state => state.Advertisement);
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(getListAdvertisement());
    }, [dispatch]);

    console.log(listAdvertisement);



    return (
        <>
            <CardToolbox>
                <PageHeader
                    ghost
                    title="List Advertisement"
                    subTitle={
                        <>
                            <span className="title-counter">274 Users </span>
                            <AutoComplete

                                placeholder="Search by Name"
                                width="100%"
                                patterns
                            />
                        </>
                    }
                    buttons={[
                        <Button className="btn-add_new" size="default" type="primary" key="1">
                            <Link to="/admin/addAdvertisement">+ Add New Advertisement</Link>
                        </Button>
                    ]}
                />
            </CardToolbox>

            <Main>
                <Row gutter={15}>
                    <Col md={24}>
                        <AdvertisementTable pages={listAdvertisement.docs} />
                    </Col>
                </Row>
            </Main>
        </>
    );
};

export default ListAdvertisement;
