/* eslint-disable prettier/prettier */
import axios from 'axios';
import Cookies from 'js-cookie';
import actions from './actions';

const { addNews, getListNewsBegin, getnewsItem } = actions;

const addNewNews = data => {
    return async dispatch => {
        try {
            dispatch(getListNewsBegin());
            const response = await axios.post('http://20.218.124.106:1885/api/v1/admin/addNews', data, {
                headers: {
                    'Content-Type': 'application/json',
                    token: Cookies.get('token'),
                },
            });
            if (response.status === 200) dispatch(addNews(response.data));
        } catch (err) {
            dispatch(addNews(err.response.data));
        }
    };
};


const getNews = (category) => {
    return async dispatch => {
        try {
            const response = await axios.get(`http://20.218.124.106:1885/api/v1/user/listNews?category=${category}`, {
                headers: {
                    'Content-Type': 'application/json',
                    token: Cookies.get('token'),
                },
            });
            dispatch(getnewsItem(response.data))        }
        catch (err) {
            console.log(err)
        }

    }
}



export { addNewNews, getNews };
