/* eslint-disable prettier/prettier */
import { Button, Col, Form, Input, notification, Row, Select } from 'antd';
import TextArea from 'antd/lib/input/TextArea';
import { Option } from 'antd/lib/mentions';

import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom/cjs/react-router-dom.min';
import { useDispatch, useSelector } from 'react-redux';
import { Cards } from '../../components/cards/frame/cards-frame';

import { PageHeader } from '../../components/page-headers/page-headers';
import { FormValidationWrap, VerticalFormStyleWrap } from '../forms/overview/Style';

import { Main } from '../styled';
import { editNews } from '../../redux/news/actionCreator';

const EditNews = () => {
    const dispatch = useDispatch();
    const [form] = Form.useForm();

    const { newsId } = useParams();
    const { errorMessageEdit } = useSelector(state => state.News)

    const openNotificationWithIcon = type => {
        notification[type]({
            message: 'Notification',
            description: errorMessageEdit.responseMessage
        });
    };





    const submmit = values => {
        dispatch(editNews({
            title: values.title,
            description: values.description,
            status: values.status,
            id: newsId
        }))
    }
    useEffect(() => {
    }, [dispatch]);

    useEffect(() => {
        if (errorMessageEdit) {

            if (errorMessageEdit?.responseCode === 200) {
                openNotificationWithIcon('success');
            }
            else {
                openNotificationWithIcon('error');
            }
        }
    }, [errorMessageEdit]);
    return (
        <>
            <PageHeader
                ghost
                title="Edit Advertisement"
                buttons={[
                    <Button className="btn-add_new" size="default" type="primary" key="1" >
                        <Link to="/admin/listAdvertisement">List Advertisement</Link>
                    </Button>
                ]} />

            <Main>
                <Row gutter={25}>
                    <Col xs={24}>
                        <FormValidationWrap>
                            <VerticalFormStyleWrap>
                                <Cards>
                                    <Form name="sDash_validation-form" form={form} layout="vertical" onFinish={submmit}>
                                        <Row gutter={30}>
                                            <Col md={12} xs={24}>
                                                <Form.Item
                                                    name="title"
                                                    label="Title"
                                                    rules={[
                                                        { min: 10, message: 'must be at least 10 characters!' }
                                                    ]}
                                                >
                                                    <Input placeholder="Enter title" />
                                                </Form.Item>
                                            </Col>

                                            {/* <Col md={8} xs={24}>
                                                <Form.Item name="category" label="Category">
                                                    <Select size="large" className="sDash_fullwidth-select" placeholder="Category">
                                                        <Option value="product" >Product</Option>
                                                        <Option value="company ">Company</Option>
                                                        <Option value="announcement ">Announcement</Option>
                                                        <Option value="bug Fix ">Bug Fix</Option>

                                                    </Select>
                                                </Form.Item>
                                            </Col> */}


                                            <Col md={12} xs={24}>
                                                <Form.Item name="status" label="Status" >
                                                    <Select size="large" className="sDash_fullwidth-select" placeholder="Status">
                                                        <Option value="ACTIVE" >ACTIVE</Option>
                                                        <Option value="CANCEL">CANCEL</Option>
                                                    </Select>
                                                </Form.Item>
                                            </Col>

                                        </Row>
                                        <div className="sDash_agree-check">
                                            <Form.Item name="description" label="Description" rules={[{ min: 10, message: 'must be at least 10 characters!' }]} >
                                                <TextArea className="sDash_unresizable" placeholder='Enter Description' />
                                            </Form.Item>
                                        </div>
                                        <div className="sDash_form-action mt-20">
                                            <Button htmlType="submit" type="primary" size="large">
                                                Submit Form
                                            </Button>
                                        </div>
                                    </Form>
                                </Cards>
                            </VerticalFormStyleWrap>
                        </FormValidationWrap>
                    </Col>
                </Row>
            </Main>

        </>
    );
};

export default EditNews;
