/* eslint-disable no-underscore-dangle */
/* eslint-disable prettier/prettier */
import actions from './actions';

const { GET_LIST_ADVERTISEMENT, DELETE_ADVERTISEMENT, ADD_ADVERTISEMENT ,G} = actions;

const initialState = {
    listAdvertisement: [],
    totalPage: 0,
    loading: false,
    error: null,
    errorMessage: null,

};

const advertisementReducer = (state = initialState, action) => {
    const { type, data, id, errorMessageAdd } = action;
    switch (type) {
        case GET_LIST_ADVERTISEMENT:
            return {
                ...state,
                listAdvertisement: data?.docs,
                totalPage: data?.total,
                loading: false,
            };
        case DELETE_ADVERTISEMENT:
            return {
                ...state,
                listAdvertisement: state.listAdvertisement.filter(item => item._id !== id),
            };
        case ADD_ADVERTISEMENT:
            return {
                ...state,
                errorMessage: errorMessageAdd,
            };

        default:
            return state;
    }
}

export default advertisementReducer;
