/* eslint-disable prettier/prettier */
import { Button, Col, DatePicker, Form, Input, notification, Row, Select, Upload } from 'antd';
import axios from 'axios';
import FormData from 'form-data';
import TextArea from 'antd/lib/input/TextArea';
import React from 'react';
import { Option } from 'antd/lib/mentions';
import { Link } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { FormWrapperNoMargin } from './Style';
import { Cards } from '../../components/cards/frame/cards-frame';
import { PageHeader } from '../../components/page-headers/page-headers';
import { FormValidationWrap, HorizontalFormStyleWrap, VerticalFormStyleWrap } from '../forms/overview/Style';
import { Main } from '../styled';
import { addAdvertisement } from '../../redux/advertisement/actionCreator';

const AddAdvertisement = () => {
    const dispatch = useDispatch();
    const formData = new FormData();
    const [form] = Form.useForm();

    const openNotificationWithIcon = type => {
        notification[type]({
            message: 'Notification Title',
            description:
                'This is the content of the notification. This is the content of the notification. This is the content of the notification.',
        });
    };



    const getvalue = async (value) => {
        formData.append('title', value.title);
        formData.append('description', value.description);
        formData.append('startDate', value.startdate);
        formData.append('endDate', value.enddate);
        formData.append('image', value.avatar.file);
        formData.append('adPosition', value.positionSelect);
        formData.append('adType', value.typeSelect);
        formData.append('adURL', value.AdURL);

        dispatch(addAdvertisement(formData));

        // remove form data after submit
        formData.delete('title');
        formData.delete('description');
        formData.delete('startDate');
        formData.delete('endDate');
        formData.delete('image');
        formData.delete('adPosition');
        formData.delete('adType');
        formData.delete('adURL');


    }

    return (
        <>
            <PageHeader
                ghost
                title="Add Advertisement"
                buttons={[
                    <Button className="btn-add_new" size="default" type="primary" key="1">
                        <Link to="/admin/listAdvertisement">List Advertisement</Link>
                    </Button>
                ]} />
            <Main>
                <Row gutter={25}>
                    <Col xs={24}>
                        <FormValidationWrap>
                            <VerticalFormStyleWrap>
                                <Cards title="Custom Styles">
                                    <Form name="sDash_validation-form" form={form} layout="vertical" onFinish={getvalue}>
                                        <Row gutter={30}>
                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="title"
                                                    label="Title"
                                                    rules={[
                                                        { required: true, message: 'First Name required!' },
                                                        { min: 10, message: 'must be at least 10 characters!' }
                                                    ]}
                                                >
                                                    <Input placeholder="Enter title" />
                                                </Form.Item>
                                            </Col>


                                            <Col md={8} xs={24}>
                                                <FormWrapperNoMargin>
                                                    <HorizontalFormStyleWrap className="sDash_input-form">
                                                        <Form.Item name="startdate"
                                                            label="Start Date"
                                                            rules={[{ required: true, message: 'Start Date required!' }]}>
                                                            <DatePicker placeholder='Start Date' />
                                                        </Form.Item>
                                                    </HorizontalFormStyleWrap>
                                                </FormWrapperNoMargin>
                                            </Col>


                                            <Col md={8} xs={24}>
                                                <FormWrapperNoMargin>
                                                    <HorizontalFormStyleWrap className="sDash_input-form">
                                                        <Form.Item name="enddate"
                                                            label="End Date"
                                                            rules={[{ required: true, message: 'End Date required!' }]}>
                                                            <DatePicker placeholder='End Date' />
                                                        </Form.Item>
                                                    </HorizontalFormStyleWrap>
                                                </FormWrapperNoMargin>
                                            </Col>


                                            <Col md={8} xs={24}>
                                                <Form.Item name="positionSelect" label="Ad Position" rules={[{ required: true, message: 'Ad Position required!' }]}>
                                                    <Select size="large" className="sDash_fullwidth-select" placeholder="Select Position" >
                                                        <Option value="Home" >Home</Option>
                                                        <Option value="Token">Token</Option>
                                                    </Select>
                                                </Form.Item>
                                            </Col>

                                            <Col md={8} xs={24}>
                                                <Form.Item name="typeSelect" label="Ad Type" rules={[{ required: true, message: 'Ad Type required!' }]}>
                                                    <Select size="large" className="sDash_fullwidth-select" placeholder="Select Type">
                                                        <Option value="Mobile" >Mobile</Option>
                                                        <Option value="Desktop">Desktop</Option>
                                                    </Select>
                                                </Form.Item>
                                            </Col>

                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="AdURL"
                                                    label="Ad URL"
                                                    rules={[{ required: true, message: 'Ad URL required!' }]}
                                                >
                                                    <Input placeholder="Enter Ad URL" />
                                                </Form.Item>
                                            </Col>
                                            <Col md={8} xs={24}>
                                                <Form.Item
                                                    name="avatar"
                                                    rules={[{ required: true, message: 'Ad Image required!' }]}
                                                >
                                                    <Upload
                                                        name="avatar"
                                                        beforeUpload={() => false}
                                                        className="sDash_upload-basic"
                                                        accept="image/*"
                                                        maxCount={1}
                                                    >
                                                        <span className="sDash_upload-text">Select File</span>
                                                        <Link to="#" className="sDash_upload-browse">
                                                            Browse
                                                        </Link>
                                                    </Upload>
                                                </Form.Item>
                                            </Col>
                                        </Row>

                                        <div className="sDash_agree-check">
                                            <Form.Item name="description" label="Description" rules={[{ required: true, message: 'Description required!' }, { min: 10, message: 'must be at least 10 characters!' }]}>
                                                <TextArea className="sDash_unresizable" placeholder='Enter Description' />
                                            </Form.Item>
                                        </div>

                                        <div className="sDash_form-action mt-20">
                                            <Button htmlType="submit" type="primary" size="large">
                                                Submit Form
                                            </Button>
                                        </div>
                                    </Form>
                                </Cards>
                            </VerticalFormStyleWrap>
                        </FormValidationWrap>
                    </Col>
                </Row>
            </Main>
        </>
    );
};

export default AddAdvertisement;
