import Cookies from 'js-cookie';
import actions from './actions';

const { FORGET_PASSWORD } = actions;

const initState = {
    email : '',
    loading: false,
    token: ""
}

const ForgetReducer = (state = initState, action) => {
 const { type, data, err } = action;
  switch (type) {
        case FORGET_PASSWORD:
            return {
                ...state,
                email: data,
                loading: false,
            };
        default:
            return state;
    }
}
export default ForgetReducer;
