/* eslint-disable no-unused-vars */
/* eslint-disable prettier/prettier */
import { Button, Row, Col } from 'antd';
import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';

import { useDispatch, useSelector } from 'react-redux';
import NewsTable from './NewsTable';
import { PageHeader } from '../../components/page-headers/page-headers';
import { CardToolbox, Main } from '../styled';
import { AutoComplete } from '../../components/autoComplete/autoComplete';
import { getNews, searchNews } from '../../redux/news/actionCreator';



const ListNews = () => {
    const dispatch = useDispatch();
    const { newsData } = useSelector(state => state.News)

    useEffect(() => {
        dispatch(getNews("All"))
    }, [dispatch])

    return (
        <>
            <CardToolbox>
                <PageHeader
                    ghost
                    title="List News"
                    subTitle={
                        <>
                            <AutoComplete
                                placeholder="Search by Title || Description"
                                width="100%"
                                patterns
                                onSearch={value => value.length === 0 ? dispatch(getNews('All')) :  dispatch(searchNews(value))}
                            />
                        </>
                    }
                    buttons={[
                        <Button className="btn-add_new" size="default" type="primary" key="1">
                            <Link to="/admin/AddNews">+ Add News</Link>
                        </Button>
                    ]}
                />
            </CardToolbox>

            <Main>
                <Row gutter={15}>
                    <Col md={24}>
                        {newsData ? (
                            <NewsTable />
                        ) : null}
                    </Col>
                </Row>
            </Main>
        </>
    );
};

export default ListNews;
