/* eslint-disable react/prop-types */
/* eslint-disable no-unused-vars */
import { Badge, Col, Row, Table } from 'antd';
import React from 'react';
import { EyeOutlined } from '@ant-design/icons';
import { GiClick } from 'react-icons/gi';
import CalenDar from 'react-calendar';
import { useSelector } from 'react-redux';
import moment from 'moment';
import { CalendarWrapper, Main } from '../styled';
import { ViewModals } from '../../components/modals/View-modals';
import Heading from '../../components/heading/heading';
import { Focard, CardBarChart2, EChartCard, PerformanceChartWrapper } from '../dashboard/style';
import { Aside } from '../calendar/Style';
import 'react-calendar/dist/Calendar.css';
import { Cards } from '../../components/cards/frame/cards-frame';

const AdsModalEdit = ({ modalType, visible, handleCancel }) => {
  const { dataAds } = useSelector(state => state.Advertisement);
  const dataSource = [
    {
      key: '1',
      adposition: `${dataAds.adPosition}`,
      type: `${dataAds.adType}`,
      discription: `${dataAds.description}`,
      viewmode: 'Mobile',
    },
  ];
  const dataSource2 = [
    {
      key: '1',
      startdate: `${moment(dataAds.startDate).format('DD/MM/YYYY HH:mm')}`,
      impressionlimit: `${dataAds.impressionLimit}`,
      enddate: `${moment(dataAds.endDate).format('DD/MM/YYYY HH:mm')}`,
      status: `${dataAds.status}`,
      url: `${dataAds.adURL}`,
    },
  ];

  const columnsPosition = [
    {
      title: 'Ad Position',
      dataIndex: 'adposition',
      key: 'adposition',
      align: 'center',
    },
    {
      title: 'Type',
      dataIndex: 'type',
      key: 'type',
      align: 'center',
    },
    {
      title: 'Discription',
      dataIndex: 'discription',
      key: 'discription',
      align: 'center',
    },
    {
      title: 'View Mode',
      dataIndex: 'viewmode',
      key: 'viewmode',
      align: 'center',
    },
  ];

  const columnsDate = [
    dataAds.adType !== 'Impression'
      ? {
          title: 'Start Date',
          dataIndex: 'startdate',
          key: 'startdate',
          align: 'center',
        }
      : {
          title: 'Impression Limit',
          dataIndex: 'impressionlimit',
          key: 'impressionlimit',
          align: 'center',
        },
        dataAds.adType === 'Date' &&{
      title: 'End Date',
      dataIndex: 'enddate',
      key: 'enddate',
      align: 'center',
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      align: 'center',
    },
    {
      title: 'URL',
      dataIndex: 'url',
      key: 'url',
      align: 'center',
    },
  ];

  return (
    <Main>
      <Row gutter={15}>
        <Col md={12}>
          <ViewModals type={modalType} title="View Informaton" visible={visible} onCancel={handleCancel} width={1100}>
            <Row gutter={25} className="focard-wrapper">
              <Col lg={6} md={12} sm={12} xs={24} style={{ paddingRight: 5, paddingLeft: 5 }}>
                <Cards headless>
                  <EChartCard>
                    <CardBarChart2>
                      <div style={{ display: 'flex', flexDirection: 'row', marginBottom: 10 }}>
                        <span style={{ marginRight: 10 }}>
                          <EyeOutlined className="growth-upward" style={{ color: '#20C997' }} />
                        </span>
                        <span>Site Impressions</span>
                      </div>
                      <Heading as="h1">{dataAds.siteImpressionCount}</Heading>
                    </CardBarChart2>
                  </EChartCard>
                </Cards>
              </Col>
              <Col lg={6} md={12} sm={12} xs={24} style={{ paddingRight: 5, paddingLeft: 5 }}>
                <Cards headless>
                  <EChartCard>
                    <CardBarChart2>
                      <div style={{ display: 'flex', flexDirection: 'row', marginBottom: 10 }}>
                        <span style={{ marginRight: 10 }}>
                          <GiClick className="growth-upward" style={{ color: '#20C997' }} />
                        </span>
                        <span>Site Click Count</span>
                      </div>
                      <Heading as="h1">{dataAds.siteClickCount}</Heading>
                    </CardBarChart2>
                  </EChartCard>
                </Cards>
              </Col>
              <Col lg={6} md={12} sm={12} xs={24} style={{ paddingRight: 5, paddingLeft: 5 }}>
                <Cards headless>
                  <EChartCard>
                    <CardBarChart2>
                      <div style={{ display: 'flex', flexDirection: 'row', marginBottom: 10 }}>
                        <span style={{ marginRight: 10 }}>
                          <EyeOutlined className="growth-upward" style={{ color: '#20C997' }} />
                        </span>
                        <span>Mobile Impressions</span>
                      </div>
                      <Heading as="h1">{dataAds.mobImpressionCount}</Heading>
                    </CardBarChart2>
                  </EChartCard>
                </Cards>
              </Col>
              <Col lg={6} md={12} sm={12} xs={24} style={{ paddingRight: 5, paddingLeft: 5 }}>
                <Cards headless>
                  <EChartCard>
                    <CardBarChart2>
                      <div style={{ display: 'flex', flexDirection: 'row', marginBottom: 10 }}>
                        <span style={{ marginRight: 10 }}>
                          <GiClick className="growth-upward" style={{ color: '#20C997' }} />
                        </span>
                        <span>Mobile Click Count</span>
                      </div>
                      <Heading as="h1">{dataAds.mobClickCount}</Heading>
                    </CardBarChart2>
                  </EChartCard>
                </Cards>
              </Col>
            </Row>
            <Row gutter={25} style={{ paddingTop: '30px', marginTop: '10px', borderTop: '1px solid #e8e8e8' }}>
              <Col md={18} xl={18}>
                <Row gutter={25}>
                  <Table
                    dataSource={dataSource}
                    className="table-responsive"
                    pagination={false}
                    columns={columnsPosition}
                    style={{
                      width: '100%',
                    }}
                  />
                </Row>
                <Row gutter={25} style={{ marginTop: 30 }}>
                  <Table
                    dataSource={dataSource2}
                    className="table-responsive"
                    pagination={false}
                    columns={columnsDate}
                    style={{
                      width: '100%',
                    }}
                  />
                </Row>
              </Col>
              <Col xl={6} md={6}>
                <img
                  src={dataAds.image.url}
                  alt="ads"
                  style={{ width: '100%', height: 125, objectFit: 'cover', marginBottom: 10 }}
                />
                <img src={dataAds.image.url} alt="ads" style={{ width: '100%', height: 125, objectFit: 'cover' }} />
              </Col>
            </Row>
          </ViewModals>
        </Col>
      </Row>
    </Main>
  );
};

export default AdsModalEdit;
