/* eslint-disable no-underscore-dangle */
/* eslint-disable prettier/prettier */
import axios from 'axios';
import Cookies from 'js-cookie';
import actions from './actions';

const { getAdvertisement, deleteAds, addAds, getListAdvertisementBegin, serchAds, serchAdvertisementBegin, errorAdvertisementSearch, getAdsById, editTypeAds, editAds } = actions;

// get list data of advertisement
const getListAdvertisement = (pages, limted) => {
    return async dispatch => {
        try {
            dispatch(serchAdvertisementBegin());
            const response = await axios.get(`http://20.218.124.106:1885/api/v1/admin/advertisementList?page=${pages}&limit=${limted}`,
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'token': Cookies.get('token')
                    }
                });
            dispatch(getAdvertisement(response.data.result));
        } catch (err) {
            console.log(err);
        }
    };
};

const loadingStart = () => {
    return async dispatch => {
        dispatch(serchAdvertisementBegin());
    }
};



const serchAdvertisement = (value) => {
    return async dispatch => {
        try {
            dispatch(serchAdvertisementBegin());

            const response = await axios.get(`http://20.218.124.106:1885/api/v1/admin/advertisementList?search=${value}`,
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'token': Cookies.get('token')
                    }
                });

            dispatch(serchAds(response.data.result));

        } catch (err) {
            dispatch(errorAdvertisementSearch());
        }
    };
}


// delete item advertisement
const deleteAdvertisement = (id) => {
    return async dispatch => {
        try {
            const response = await axios.delete(`http://20.218.124.106:1885/api/v1/admin/deleteAdvertisment/${id}`,
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'token': Cookies.get('token')
                    }
                });
            dispatch(deleteAds(id));
        } catch (err) {
            console.log(err);
        }
    };
}


// add advertisement
const addAdvertisement = (data) => {
    return async dispatch => {
        try {
            dispatch(getListAdvertisementBegin());
            const response = await axios.post('http://20.218.124.106:1885/api/v1/admin/addAdvertisment', data,
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'token': Cookies.get('token')
                    }
                });

            if (response.status === 200) dispatch(addAds(response.data));

        } catch (err) {
            dispatch(addAds(err.response.data));

        }
    };
}

const getAdvertisementById = (id) => {
    return async dispatch => {
        try {
            const response = await axios.get(`http://20.218.124.106:1885/api/v1/admin/viewAdvertisment/${id}`,
                {
                    headers: {
                        'Content-Type': 'application/json',
                        'token': Cookies.get('token')
                    }
                });
            dispatch(getAdsById(response.data.result));
        }
        catch (err) {
            console.log(err);
        }

    }
};

const editTypeAdvertisement = (type) => {
    return async dispatch => {
        dispatch(editTypeAds(type));
    }
}

const editAdvertisement = (data) => {
    return async dispatch => {
        try {
            const response = await axios.put(`http://20.218.124.106:1885/api/v1/admin/editAdvertisment`, data, {
                headers: {
                    'Content-Type': 'application/json',
                    'token': Cookies.get('token')
                }
            },
            );
            if (response.status === 200)  dispatch(editAds(response.data));
        } catch (err) {
            dispatch(editAds(err.response.data));
        }
    }
}







export { getListAdvertisement, deleteAdvertisement, addAdvertisement, serchAdvertisement, loadingStart, getAdvertisementById, editTypeAdvertisement, editAdvertisement };
