import actions from './actions';

const { ADD_NEWS, GET_LIST_NEWS_BEGIN } = actions;

const initialState = {
  newsData: [],
  loading: false,
};
const newsReducer = (state = initialState, action) => {
  const { type, errorMessageAdd } = action;

  switch (type) {
    case GET_LIST_NEWS_BEGIN:
      return {
        ...state,
        loading: true,
      };
    case ADD_NEWS:
      return {
        ...state,
        loading: false,
        errorMessage: errorMessageAdd,
      };

    default:
      return state;
  }
};

export default newsReducer;
